function openMobMenu(){const e=document.getElementById("nav-menu"),t=document.getElementById("nav-toggle"),s=document.getElementById("nav-close"),n=document.querySelectorAll(".nav__link");t&&t.addEventListener("click",(()=>{e.classList.add("show-menu")})),s&&s.addEventListener("click",(()=>{e.classList.remove("show-menu")})),n.forEach((t=>{t.addEventListener("click",(()=>{e.classList.remove("show-menu")}))}))}function scrollHeader(){const e=document.getElementById("header");this.scrollY>=50?e.classList.add("scroll-header"):e.classList.remove("scroll-header")}openMobMenu(),window.addEventListener("scroll",scrollHeader);const productsLinks=document.querySelectorAll(".products__item");function changeProducts(e){const t=e.currentTarget,s=t.dataset.product;document.querySelectorAll(".products__content").forEach((e=>{e.classList.remove("active-content")})),productsLinks.forEach((e=>{e.classList.remove("active-link")})),document.querySelector(`#${s}`).classList.add("active-content"),t.classList.add("active-link")}productsLinks.forEach((e=>{e.addEventListener("click",changeProducts)}));const slides=[...document.querySelectorAll(".slide__container")],prevBtn=document.querySelector(".prev__slide"),nextBtn=document.querySelector(".next__slide");let index=0;const nextSlide=()=>{slides[index].classList.remove("active-slide"),index=(index+1)%slides.length,slides[index].classList.add("active-slide")},prevSlide=()=>{slides[index].classList.remove("active-slide"),index=(index-1+slides.length)%slides.length,slides[index].classList.add("active-slide")};prevBtn.addEventListener("click",prevSlide),nextBtn.addEventListener("click",nextSlide);